<?php/* ! \par		Info Generali: * \author		Luca Gianneschi * \version		1.0 * \date		2013 * \copyright		Jamyourself.com 2013 * \par			Info Classe: * \brief		box caricamento info album * \details		Recupera le informazioni dell'album, le inserisce in un array da passare alla view * \par			Commenti: * \warning * \bug * \todo		 * */if (!defined('ROOT_DIR'))    define('ROOT_DIR', '../');require_once ROOT_DIR . 'config.php';/** * \brief	AlbumBox class  * \details	box class to pass info to the view  */class AlbumBox {    public $albumArray;    public $config;    public $error;    public $imageArray;    /**     * \fn	__construct()     * \brief	class construct to import config file     */    function __construct() {	$this->config = json_decode(file_get_contents(CONFIG_DIR . "boxes/album.config.json"), false);    }    /**     * \fn	initForPersonalPage($objectId, $type)     * \brief	Init AlbumBox instance for Personal Page     * \param	$objectId for user that owns the page, $type     * \todo         */    public function init($objectId) {	require_once CLASSES_DIR . 'albumParse.class.php';	$this->imageArray = array();	$album = new AlbumParse();	$album->wherePointer('fromUser', '_User', $objectId);	$album->where('active', true);	$album->setLimit($this->config->limitForPersonalPage);	$album->orderByDescending('createdAt');	$albums = $album->getAlbums();	if ($albums instanceof Error) {	    $this->error = $albums->getErrorMessage();	    $this->albumArray = array();	    return;	} elseif (is_null($albums)) {	    $this->error = null;	    $this->albumArray = array();	    return;	} else {	    $this->error = null;	    $this->albumArray = $albums;	}    }    /**     * \fn	initForDetail($objectId $limit - optional, $skip - optional)     * \brief	Init AlbumBox instance for Personal Page, detailed view     * \param	$objectId of the album to display information      */    public function initForDetail($objectId, $limit = DEFAULTQUERY, $skip = 0) {	require_once CLASSES_DIR . 'imageParse.class.php';	$this->albumArray = array();	$image = new ImageParse();	$image->wherePointer('album', 'Album', $objectId);	$image->where('active', true);	$image->setLimit((!is_null($limit) && is_int($limit) && $limit >= MIN && MAX <= $limit) ? $limit : $this->config->limitForDetail);	$image->setSkip((!is_null($skip) && is_int($skip)) ? $skip : 0);	$image->orderByDescending('createdAt');	$images = $image->getImages();	if ($images instanceof Error) {	    $this->error = $images->getErrorMessage();	    $this->albumArray = array();	    return;	} elseif (is_null($images)) {	    $this->error = null;	    $this->albumArray = array();	    return;	} else {	    $this->error = null;	    $this->imageArray = $images;	}    }}?>