<?php/* ! \par		Info Generali: * \author		Luca Gianneschi * \version		1.0 * \date		2013 * \copyright		Jamyourself.com 2013 * \par			Info Classe: * \brief		box caricamento info event * \details		Recupera le informazioni dell'evento, le inserisce in un array da passare alla view * \par			Commenti: * \warning * \bug * \todo		 * */if (!defined('ROOT_DIR'))    define('ROOT_DIR', '../');require_once ROOT_DIR . 'config.php';require_once CLASSES_DIR . 'record.class.php';require_once CLASSES_DIR . 'recordParse.class.php';/** * \brief	RecordBox class  * \details	box class to pass info to the view for personal page, media page & uploadRecord page */class RecordBox {    public $config;    public $error;    public $recordArray;    /**     * \fn	__construct()     * \brief	class construct to import config file     */    function __construct() {        $this->config = json_decode(file_get_contents(CONFIG_DIR . "boxes/record.config.json"), false);    }    /**     * \fn	initForMediaPage($objectId)     * \brief	init for Media Page     * \param	$objectId of the record to display in Media Page     * \todo         */    public function initForMediaPage($objectId) {        $recordP = new RecordParse();        $recordP->where('objectId', $objectId);        $recordP->where('active', true);        $recordP->whereInclude('fromUser');        $recordP->setLimit($this->config->limitRecordForMediaPage);        $records = $recordP->getRecords();        if ($records instanceof Error) {            $this->error = $records->getErrorMessage();            $this->recordArray = array();            return;        } elseif (is_null($records)) {            $this->error = null;            $this->recordArray = array();            return;        } else {            $this->error = null;            $this->recordArray = $records;        }    }    /**     * \fn	initForPersonalPage($objectId)     * \brief	init for recordBox for personal Page     * \param	$objectId of the user who owns the page     * \todo	     */    public function initForPersonalPage($objectId, $limit = null, $skip = null) {        $record = new RecordParse();        $record->wherePointer('fromUser', '_User', $objectId);        $record->where('active', true);        $record->setLimit((!is_null($limit) && is_int($limit) && $limit >= MIN && MAX >= $limit) ? $limit : DEFAULTQUERY);        $record->setSkip((!is_null($skip) && is_int($skip) && $skip >= 0) ? $skip : 0);        $record->orderByDescending('createdAt');        $records = $record->getRecords();        if ($records instanceof Error) {            $this->error = $records->getErrorMessage();            $this->recordArray = array();            return;        } elseif (is_null($records)) {            $this->error = null;            $this->recordArray = array();            return;        } else {            $this->error = null;            $this->recordArray = $records;        }    }    /**     * \fn	initForUploadRecordPage($objectId)     * \brief	init for recordBox for upload record page     * \param	$objectId of the user who owns the record     */    public function initForUploadRecordPage() {        global $boxes;        require_once BOXES_DIR . 'utilsBox.php';        $currentUserId = sessionChecker();        if (is_null($currentUserId)) {            global $boxes;            $this->errorManagement($boxes['ONLYIFLOGGEDIN']);            return;        }        $record = new RecordParse();        $record->wherePointer('fromUser', '_User', $currentUserId);        $record->where('active', true);        $record->setLimit($this->config->limitRecordForUploadRecordPage);        $record->orderByDescending('createdAt');        $records = $record->getRecords();        if ($records instanceof Error) {            $this->error = $records->getErrorMessage();            $this->recordArray = array();            return;        } elseif (is_null($records)) {            $this->error = null;            $this->recordArray = array();            return;        } else {            $this->error = null;            $this->recordArray = $records;        }    }}?>