<?php/* ! \par                Info Generali: * \author                Luca Gianneschi * \version                1.0 * \date                2013 * \copyright                Jamyourself.com 2013 * \par                        Info Classe: * \brief                box caricamento review event * \details                Recupera le informazioni sulla review dell'event, le inserisce in un array da passare alla view * \par                        Commenti: * \warning * \bug * \todo                 */if (!defined('ROOT_DIR'))    define('ROOT_DIR', '../');require_once ROOT_DIR . 'config.php';require_once SERVICES_DIR . 'debug.service.php';/** * \brief        ReviewBox class * \details        box class to pass info to the view */class ReviewBox {    public $config;    public $error;    public $mediaInfo;    public $reviewArray;    /**     * \fn        initForMediaPage($id, $className, $limit, $skip)     * \brief        Init ReviewBox instance for Media Page     * \param        $id of the review to display information, Event or Record class     * \param   $className, $limit, $skip,$currentUserId     * \todo             */    public function initForMediaPage($id, $className, $limit = null, $skip = null) {	$this->error = null;	$this->reviewArray = array();    }    /**     * \fn        initForPersonalPage($id, $type, $className)     * \brief        Init ReviewBox instance for Personal Page     * \param        $id of the user who owns the page, $type of user, $className Record or Event class     * \param   $type, $className     */    function initForPersonalPage($id, $type, $className, $limit = null, $skip = null) {	$this->error = null;	$this->reviewArray = array();    }    /**     * \fn        initForUploadReviewPage($id, $className)     * \brief        Init REviewBox instance for Upload Review Page     * \param        $id for the event or record, $className Record or Event     * \todo         */    public function initForUploadReviewPage($id, $className) {	require_once SERVICES_DIR . 'utils.service.php';	$currentUserId = sessionChecker();	if (is_null($currentUserId)) {	    $this->errorManagement(ONLYIFLOGGEDIN);	    return;	}	$this->error = null;	$this->reviewArray = array();    }    /**     * \fn        errorManagement($errorMessage = null)     * \brief        set values in case of error or nothing to send to the view     * \param        $errorMessage     */    private function errorManagement($errorMessage = null) {	$this->config = null;	$this->error = $errorMessage;	$this->mediaInfo = array();	$this->reviewArray = array();    }}?>