<?php/* box le activity * box chiamato tramite ajax con: * data-type: html, * type: POST o GET * */ini_set('display_errors', '1');if (!defined('ROOT_DIR'))	define('ROOT_DIR', '../../../../');require_once ROOT_DIR . 'config.php';require_once SERVICES_DIR . 'debug.service.php';require_once SERVICES_DIR . 'lang.service.php';require_once LANGUAGES_DIR . 'views/' . getLanguage() . '.views.lang.php';  require_once BOXES_DIR . 'record.box.php';$lat = $_POST['latitude'];$lon = $_POST['longitude'];$genre = $_POST['genre'];debug(DEBUG_DIR, 'debug.txt', "lat=>$lat, lon=>$lon");debug(DEBUG_DIR, 'debug.txt', 'genre=>' . json_encode($genre));$recordBox = new RecordBox();$recordBox->initForStream(array(), 'Viareggio', 'IT');$records = $recordBox->recordArray;foreach($records as $key => $value) {    debug(DEBUG_DIR, 'debug.txt', 'value=>' . json_encode((array)$value));}?>